# login
def login():
    username = entry_1.get()
    password = entry_2.get()

    try:
        connection = mysql.connector.connect(
            host='localhost',       # Replace with your MySQL server host
            user='root',   # Replace with your MySQL username
            password='',  # Replace with your MySQL password
            database='pet'   # Replace with your MySQL database name
        )
        cursor = connection.cursor()
        query = "SELECT * FROM accounts WHERE username = %s AND password = %s"
        cursor.execute(query, (username, password))
        result = cursor.fetchone()

        if result:
            messagebox.showinfo("Login Success", "You have successfully logged in.")
            main_script_path = r"c:\xampp\htdocs\pet\build\main.py"  # Full path to main.py
            if Path(main_script_path).is_file():
                subprocess.Popen(["python", main_script_path])
                window.destroy()
            else:
                messagebox.showerror("Error", f"File not found: {main_script_path}")
        else:
            messagebox.showerror("Login Failed", "Invalid username or password.")
        
        cursor.close()
        connection.close()
    
    except mysql.connector.Error as err:
        messagebox.showerror("Error", f"Error connecting to the database: {err}")
